package hu.ponte.hr;

import hu.ponte.hr.entity.ImageMetaEntity;
import hu.ponte.hr.services.ImageStoreService;
import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.params.ParameterizedTest;
import org.junit.jupiter.params.provider.Arguments;
import org.junit.jupiter.params.provider.MethodSource;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.boot.test.context.SpringBootTest;
import org.springframework.mock.web.MockMultipartFile;
import org.springframework.web.multipart.MultipartFile;

import java.io.File;
import java.io.IOException;
import java.nio.file.Files;
import java.util.LinkedHashMap;
import java.util.Map;
import java.util.stream.Stream;

/**
 * @author zoltan
 */
@SpringBootTest
public class SignTest {

    static Map<String, String> files = new LinkedHashMap<>() {
        {
            put("cat.jpg", "");
            put("enhanced-buzz.jpg", "");
            put("rnd.jpg", "");
        }
    };
    @Autowired
    private ImageStoreService imageStoreService;

    public static Stream<Arguments> saveImageTestDataProvider() {
        return files.entrySet().stream()
                .map(e -> Arguments.of(e.getKey(), e.getValue()));
        /*return Stream.of(
                Arguments.of("cat.jpg", ""),
                Arguments.of("enhanced-buzz.jpg", ""),
                Arguments.of("rnd.jpg", "")
        );*/
    }

    @ParameterizedTest
    @MethodSource("saveImageTestDataProvider")
    void saveImageTest(String filename, String signature) throws IOException {
        File file = new File("src//test//resources//images//" + filename);
        byte[] fileByte = Files.readAllBytes(file.toPath());
        String mimeType = Files.probeContentType(file.toPath());

        MultipartFile multipartFile = new MockMultipartFile(filename, filename, mimeType, fileByte);
        ImageMetaEntity savedImage = imageStoreService.saveImage(multipartFile);

        Assertions.assertEquals(filename, savedImage.getName());
        Assertions.assertEquals(signature, savedImage.getDigitalSign());
    }

}
